mapper.select:
SELECT CART_QUANTITY
FROM TBL_CART
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}

mapper.update:
UPDATE TBL_CART
SET CART_QUANTITY = CART_QUANTITY + #{cartQuantity}
WHERE PRODUCT_ID = #{productId} AND MEMBER_ID = #{memberId}

UPDATE TBL_CART
SET CART_QUANTITY = CART_QUANTITY - #{cartQuantity}
WHERE PRODUCT_ID = #{productId}
AND CART_QUANTITY >= #{quantity}

mapper.delete:
DELETE FROM TBL_CART
WHERE MEMBER_ID = #{memberId}
AND PRODUCT_ID = #{productId}
