mapper.select:
SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_ADDRESS, MEMBER_GENDER, MEMBER_BIRTH, MEMBER_CANDY, MEMBER_RANK, MEMBER_PROVIDER, MEMBER_PHONE
FROM TBL_MEMBER
WHERE ID = #{id}

SELECT CASE WHEN COUNT(ID) > 0 THEN 1 ELSE 0 END
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.insert:
INSERT INTO TBL_MEMBER(
ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PASSWORD,
MEMBER_ADDRESS, MEMBER_PICTURE_PATH, MEMBER_PICTURE_NAME,
MEMBER_GENDER, MEMBER_BIRTH, MEMBER_PROVIDER, MEMBER_PHONE
) VALUES (
SEQ_MEMBER.NEXTVAL, #{memberName}, #{memberNickname}, #{memberEmail}, #{memberPassword},
#{memberAddress}, #{memberPicturePath}, #{memberPictureName},
#{memberGender}, #{memberBirth}, #{memberProvider}, #{memberPhone}
)

INSERT INTO TBL_MEMBER(
ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL,
MEMBER_PICTURE_PATH, MEMBER_PICTURE_NAME,
MEMBER_PROVIDER
) VALUES (
SEQ_MEMBER.NEXTVAL, #{memberName}, #{memberNickname}, #{memberEmail},
#{memberPicturePath}, #{memberPictureName},
#{memberProvider}
)
