mapper.select:
SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_ADDRESS, MEMBER_DETAIL_ADDRESS, MEMBER_POSTCODE, MEMBER_GENDER, MEMBER_BIRTH, MEMBER_CANDY, MEMBER_RANK, MEMBER_PROVIDER, MEMBER_PHONE
FROM TBL_MEMBER
WHERE ID = #{id}

SELECT CASE WHEN COUNT(ID) > 0 THEN 1 ELSE 0 END
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_PROFILE_PATH, MEMBER_PROFILE_NAME, MEMBER_ID
FROM TBL_MEMBER_PROFILE
WHERE MEMBER_ID = #{memberId}

mapper.insert:

    SELECT SEQ_MEMBER.NEXTVAL FROM DUAL

INSERT INTO TBL_MEMBER(
ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PHONE, MEMBER_PASSWORD,
MEMBER_ADDRESS, MEMBER_DETAIL_ADDRESS, MEMBER_POSTCODE,
MEMBER_GENDER, MEMBER_BIRTH, MEMBER_PROVIDER
) VALUES (
#{id}, #{memberName}, #{memberNickname}, #{memberEmail}, #{memberPhone}, #{memberPassword},
#{memberAddress}, #{memberDetailAddress}, #{memberPostcode},
#{memberGender}, #{memberBirth}, #{memberProvider}
)

INSERT INTO TBL_MEMBER(
ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL,
MEMBER_PROVIDER
) VALUES (
SEQ_MEMBER.NEXTVAL, #{memberName}, #{memberNickname}, #{memberEmail},
#{memberProvider}
)
