mapper.select:
SELECT MEMBER_CANDY
FROM TBL_MEMBER
WHERE ID = #{memberId}

SELECT TBP.*, TBO.total_price
FROM   TBL_PAYMENT TBP
JOIN   TBL_ORDER TBO ON TBO.id = TBP.order_id
WHERE  TBP.member_id = #{memberId}
ORDER BY TBP.PAYMENT_CREATE_AT DESC;

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_CANDY = MEMBER_CANDY - #{amount}
WHERE ID = #{memberId}

mapper.select:
SELECT PAYMENT_PRICE
FROM TBL_PAYMENT
WHERE MERCHANT_UID = #{merchantUid}

mapper.update:
UPDATE TBL_PAYMENT
SET PAYMENT_STATUS = #{status}
WHERE MERCHANT_UID = #{merchantUid}

UPDATE TBL_PAYMENT
SET IMP_UID = #{impUid},
PAID_AMOUNT = #{paidAmount},
PAYMENT_STATUS = #{status}
WHERE MERCHANT_UID = #{merchantUid}

mapper.select:
SELECT ID, PAYMENT_PRICE, PAYMENT_TYPE, PAYMENT_STATUS, PAYMENT_CREATE_AT, ORDER_ID
FROM TBL_PAYMENT
WHERE ID = #{id}

mapper.update:
UPDATE TBL_PAYMENT
SET PAYMENT_STATUS = #{paymentStatus}
WHERE ID = #{id}
