mapper.select:
SELECT ID, SOM_TITLE, SOM_CATEGORY, SOM_ADDRESS, SOM_TYPE, SOM_START_DATE, SOM_END_DATE, SOM_LIKE, SOM_CONTENT, MEMBER_ID
FROM TBL_SOM
WHERE ID = #{id}

SELECT ID, SOM_TITLE, SOM_CATEGORY, SOM_ADDRESS, SOM_TYPE, SOM_START_DATE, SOM_END_DATE, SOM_LIKE, SOM_CONTENT, MEMBER_ID
FROM TBL_SOM
WHERE SOM_CATEGORY  = #{somCategory}

SELECT ID, SOM_TITLE, SOM_CATEGORY, SOM_ADDRESS, SOM_TYPE, SOM_START_DATE, SOM_END_DATE, SOM_LIKE, SOM_CONTENT, MEMBER_ID
FROM TBL_SOM
WHERE SOM_TYPE  = #{somType}

mapper.update:
UPDATE TBL_SOM
SET SOM_LIKE = SOM_LIKE + 1
WHERE ID = #{id}
