mapper.select:
SELECT ID, SOM_TITLE, SOM_CATEGORY, SOM_ADDRESS, SOM_TYPE, SOM_START_DATE, SOM_END_DATE, SOM_LIKE, SOM_CONTENT, MEMBER_ID
FROM TBL_SOM
WHERE ID = #{id}

SELECT CEIL(COUNT(ID) / 9) AS SOM_MAX_PAGE
FROM TBL_SOM


        AND SOM_CATEGORY = #{somCategory}


        AND SOM_TYPE = #{somType}

SELECT ID, SOM_TITLE, SOM_CATEGORY, SOM_ADDRESS, SOM_TYPE, SOM_START_DATE, SOM_END_DATE, SOM_LIKE, SOM_CONTENT, MEMBER_ID
FROM TBL_SOM


        AND SOM_CATEGORY = #{somCategory}


        AND SOM_TYPE = #{somType}


ORDER BY ID DESC
OFFSET (#{page} - 1) * 9 ROWS FETCH NEXT 9 ROWS ONLY

mapper.update:
UPDATE TBL_SOM
SET SOM_LIKE = SOM_LIKE + 1
WHERE ID = #{id}

mapper.select:
SELECT COUNT(ID)
FROM TBL_SOM_LIKE
WHERE SOM_ID = #{somId}
